import React, { useState } from "react"
import Tile from './Tile'

const Board = props => {
    const initializeBoard = (x, y) => {
        let board = [];

        for (let column = 0; column < x; column++) {
            board.push([]);

            for (let row = 0; row < y; row++) {
                board[column].push("Tile");
            }
        }

        return board;
    }

    const [boardArray, setBoardArray] = useState(initializeBoard(props.boardSize.x, props.boardSize.y));

    return (
        <div>{boardArray.map(row => <div>{row}</div>)}</div>
    )
}

export default Board
