import React, { useState } from "react"
import Tile from './Tile'

const Board = props => {
    const startingTiles = initializeTiles();

    const [tiles, setTiles] = useState([]);

    const initializeTiles = (x, y) => {
        let tiles = [];

        for (let i = 0; i < x; i++) {
            for (let j = 0; j < y; j++) {
                tiles.push(<Tile coords={{x:i, y:j}}/>)
            }
        }

        return tiles;
    }

    initializeTiles(props.boardSize.x, props.boardSize.y);

    return (
        {tiles}
    )
}

export default Board
