import React, { useState } from 'react'
import Draggable from 'react-draggable';

const Piece = props => {
    const [currX, setCurrX] = useState(props.pieceProps.x);
    const [currY, setCurrY] = useState(props.pieceProps.y);

    let x = Number(currX * 100);
    let y = Number(currY * 100);

    return (
        <Draggable>
            <div style={{position:'absolute', left:`${x}%`, top:`${y}%`, backgroundColor:'red'}}>
                Piece
            </div>
        </Draggable>
    )
}

export default Piece;
