import React, { useState } from 'react'
import Draggable from 'react-draggable';

const BOARD_SIZE = 6;
const SIZE_MOD = 100 / BOARD_SIZE;

const Piece = props => {
    const [currX, setCurrX] = useState(props.pieceProps.x);
    const [currY, setCurrY] = useState(props.pieceProps.y);

    let x = currX * SIZE_MOD;
    let y = currY * SIZE_MOD;
    let color = props.pieceProps.color;

    if (props.pieceProps.orientation == 'HORIZONTAL') {
        let w = props.pieceProps.size * SIZE_MOD;
        let h = 1 * SIZE_MOD;

        return (
            <Draggable axis='x'>
                <div style={{
                    position:'absolute', 
                    left:`${x}%`, 
                    top:`${y}%`, 
                    height:`${h}%`, 
                    width:`${w}%`, 
                    backgroundColor:`${color}`,
                    border:'5px solid #ccc',
                }}>
                    Piece
                </div>
            </Draggable>
        )
    }

    let h = Number(props.pieceProps.size * SIZE_MOD);
    let w = Number(1 * SIZE_MOD);

    return (
        <Draggable axis='y'>
            <div style={{
                position:'absolute', 
                left:`${x}%`, 
                top:`${y}%`, 
                height:`${h}%`, 
                width:`${w}%`, 
                backgroundColor:`${color}`,
                border:'5px solid #ccc',
            }}>
                Piece
            </div>
        </Draggable>
    )
}

export default Piece;
