import React, { useState } from "react"

const Board = props => {
    const [tiles, setTiles] = useState(initializeTiles(props.boardSize.x, props.boardSize.y));

    const initializeTiles = (x, y) => {
        for (let i = 0; i < x; i++) {
            for (let j = 0; j < y; j++) {
                tiles.push(<Tile coords={{x:i, y:j}}/>)
            }
        }
    }
}

export default Board
